import { generatePaletteTexture } from '../utils.js';

// Generate vertices for the Heighway dragon curve
function generateHeighwayDragon(iterations) {
  // The dragon curve is generated by recursively replacing line segments
  // with two segments at 45 degrees
  
  // Start with a simple line from (-0.5, 0) to (0.5, 0)
  let sequence = [1]; // 1 = forward
  
  // Build the dragon curve sequence using the paper-folding algorithm
  // At each iteration, we append the current sequence reversed with signs flipped
  for (let iter = 0; iter < iterations; iter++) {
    const newSequence = [...sequence];
    newSequence.push(1); // Add a right turn in the middle
    
    // Append the reverse of the sequence with alternating signs
    for (let i = sequence.length - 1; i >= 0; i--) {
      newSequence.push(-sequence[i]);
    }
    
    sequence = newSequence;
  }
  
  // Now convert the sequence to vertices
  // Starting position and direction
  let x = -0.5;
  let y = 0;
  let angle = 0; // Start facing right
  
  // Calculate step size to fit the curve in view
  const stepLength = 1.0 / Math.pow(Math.sqrt(2), iterations);
  
  const vertices = [];
  vertices.push(x, y);
  
  // Draw each segment
  for (let i = 0; i < sequence.length; i++) {
    // Move forward
    x += Math.cos(angle) * stepLength;
    y += Math.sin(angle) * stepLength;
    vertices.push(x, y);
    
    // Turn based on the sequence value
    // 1 = turn right (clockwise), -1 = turn left (counter-clockwise)
    angle += sequence[i] * Math.PI / 2;
  }
  
  return new Float32Array(vertices);
}

const vertexShader = `
  precision mediump float;
  attribute vec2 position;
  uniform float uZoom;
  uniform vec2 uOffset;
  uniform vec2 uResolution;
  uniform float uXScale;
  uniform float uYScale;
  varying vec2 vPosition;

  void main() {
    float aspect = uResolution.x / uResolution.y;
    float scale = 4.0 / uZoom;
    
    // Transform vertex position to match the standard fractal coordinate system
    // For line-based fractals, maintain aspect ratio correctly
    vec2 fractalCoord = position;
    vec2 relative = fractalCoord - uOffset;
    
    // Scale uniformly (same factor for x and y) to preserve shape
    vec2 scaled = vec2(
      relative.x / (scale * uXScale),
      relative.y / (scale * uYScale)
    );
    
    // Apply aspect ratio correction to maintain shape across different window sizes
    // Divide x by aspect to compensate for wider screens, preserving the curve's proportions
    scaled.x /= aspect;
    
    // Convert to clip space (-1 to 1) by multiplying by 2.0
    gl_Position = vec4(scaled * 2.0, 0.0, 1.0);
    vPosition = position;
  }
`;

const fragmentShader = `
  precision mediump float;
  uniform sampler2D uPalette;
  uniform float uIterations;
  varying vec2 vPosition;

  void main() {
    // Color based on position along the curve
    float t = fract(length(vPosition) * 1.5);
    
    vec3 color = texture2D(uPalette, vec2(t, 0.5)).rgb;
    gl_FragColor = vec4(color, 1.0);
  }
`;

export function render(regl, params, canvas) {
  // Generate palette texture for the current color scheme
  const paletteTexture = generatePaletteTexture(regl, params.colorScheme);

  // Calculate iteration level based on params.iterations
  // Map 0-200 iterations to 0-15 levels
  // Dragon curve doubles in complexity each iteration, so keep it reasonable
  const iterationLevel = Math.max(0, Math.min(15, Math.floor(params.iterations / 13)));
  
  // Generate vertices for current iteration level
  const vertices = generateHeighwayDragon(iterationLevel);

  const drawDragon = regl({
    vert: vertexShader,
    frag: fragmentShader,
    attributes: {
      position: vertices,
    },
    uniforms: {
      uZoom: params.zoom,
      uOffset: [params.offset.x, params.offset.y],
      uResolution: [canvas.width, canvas.height],
      uPalette: paletteTexture,
      uIterations: params.iterations,
      uXScale: params.xScale,
      uYScale: params.yScale,
    },
    viewport: {
      x: 0,
      y: 0,
      width: canvas.width,
      height: canvas.height,
    },
    count: vertices.length / 2,
    primitive: 'line strip',
    lineWidth: 1,
  });

  return drawDragon;
}

export const is2D = true;

/**
 * Configuration for Heighway Dragon fractal
 */
export const config = {
  initialSettings: {
    colorScheme: 'rainbow-shifted',
  },
  initialPosition: {
    zoom: 2,
    offset: { x: 0.2257, y: -0.1287 },
  },
  interestingPoints: [
    { x: 0, y: 0, zoom: 1 }, // Full dragon view
    { x: 0, y: 0, zoom: 2 }, // Center detail
    { x: 0.2, y: 0, zoom: 3 }, // Right side of dragon
    { x: -0.2, y: 0, zoom: 3 }, // Left side of dragon
    { x: 0.1, y: 0.1, zoom: 4 }, // Upper right quadrant
    { x: -0.1, y: 0.1, zoom: 4 }, // Upper left quadrant
    { x: 0.1, y: -0.1, zoom: 4 }, // Lower right quadrant
    { x: -0.1, y: -0.1, zoom: 4 }, // Lower left quadrant
    { x: 0.15, y: 0.05, zoom: 6 }, // Deep zoom right
    { x: 0, y: 0, zoom: 1.5 }, // Medium zoom center
  ],
  fallbackPosition: {
    offset: { x: 0, y: 0 },
    zoom: 1,
  },
};

